#!/usr/bin/env python3\n\"\"\"\nCheck Prajna Status\n==================\n\nQuick script to check if Prajna is running and responding\n\"\"\"\n\nimport requests\nimport time\n\ndef check_prajna_status():\n    \"\"\"Check if Prajna is responding\"\"\"\n    ports_to_check = [8001, 8000, 8002, 8003]\n    \n    for port in ports_to_check:\n        try:\n            print(f\"🔍 Checking port {port}...\")\n            \n            # Try health check\n            response = requests.get(f'http://localhost:{port}/api/health', timeout=3)\n            if response.status_code == 200:\n                print(f\"✅ Found Prajna on port {port}!\")\n                print(f\"📊 Health: {response.json()}\")\n                return port\n                \n        except requests.exceptions.RequestException as e:\n            print(f\"❌ Port {port}: {e}\")\n            continue\n    \n    print(\"🔍 Checking if Prajna process is running...\")\n    return None\n\ndef test_prajna_query(port):\n    \"\"\"Test Prajna with a simple query\"\"\"\n    try:\n        test_data = {\n            \"user_query\": \"Hello Prajna, are you working?\",\n            \"enable_reasoning\": True\n        }\n        \n        response = requests.post(\n            f'http://localhost:{port}/api/answer',\n            json=test_data,\n            timeout=10\n        )\n        \n        if response.status_code == 200:\n            result = response.json()\n            print(f\"✅ Prajna Response: {result['answer'][:100]}...\")\n            print(f\"🎯 Trust Score: {result['trust_score']:.2f}\")\n            print(f\"⚡ Processing Time: {result['processing_time']:.2f}s\")\n            return True\n        else:\n            print(f\"❌ Bad response: {response.status_code}\")\n            return False\n            \n    except Exception as e:\n        print(f\"❌ Query test failed: {e}\")\n        return False\n\nif __name__ == \"__main__\":\n    print(\"🧠 Checking Prajna Status...\")\n    \n    port = check_prajna_status()\n    \n    if port:\n        print(f\"\\n🧪 Testing Prajna functionality on port {port}...\")\n        if test_prajna_query(port):\n            print(\"\\n🎉 PRAJNA IS WORKING PERFECTLY!\")\n        else:\n            print(\"\\n⚠️ Prajna found but not responding to queries\")\n    else:\n        print(\"\\n❌ Prajna not found on any port\")\n        print(\"💡 It might still be starting up...\")\n